//FUNCTION
const deleteLeftMost = (strinput, strdelete) => {
    let changedStr = strinput.replace(strdelete, "");
    return changedStr;
};

const addRightMost = (strinput, stradd) => {
    let changeStr = strinput+stradd;
    return changeStr; 
};

const createTopicDownlink = (strTopic, strUplink, strDownlink) => {
    let changeStr = addRightMost(deleteLeftMost(strTopic, strUplink), strDownlink);
    return changeStr; //We delete the uplink suffix then add the downlink suffix in order to create the downlink topic
};
